package e.Quran.Qaz.adapter;import android.annotation.SuppressLint;import android.content.Context;import android.graphics.Typeface;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ArrayAdapter;import android.widget.TextView;import e.Quran.Qaz.R;import e.Quran.Qaz.model.Asma;import java.util.ArrayList;public class AsmaAdapter extends ArrayAdapter<Asma> {    private Typeface scheherazade;    private ArrayList<Asma> asma;    public AsmaAdapter(Context context, ArrayList<Asma> users) {        super(context, 0, users);        scheherazade = Typeface.createFromAsset(context.getAssets(), "Scheherazade.ttf");    }    @SuppressLint("DefaultLocale")    @Override    public View getView(int position, View convertView, ViewGroup parent) {        if (convertView == null)            convertView = LayoutInflater.from(parent.getContext()).inflate(R.layout.item_asma, parent, false);        Asma asma = getItem(position);        TextView nameArab = convertView.findViewById(R.id.nameOfAllahArab);        TextView nameKazakh = convertView.findViewById(R.id.nameOfAllahKazakh);        nameArab.setTypeface(scheherazade);        nameArab.setText(asma.getNameArab());        nameKazakh.setText(asma.getNameKazakh());        return convertView;    }}